targets.expr:
topk(${top}, 
    sum by(host_name, method, uri, status) (rate(http_server_requests_count{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", status!~"[1-3].."}[$__rate_interval]))
)

topk(${top}, 
    sum by (host_name) (jvm_memory_used{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", area="heap"})
    * 100 /
    sum by (host_name) (jvm_memory_max{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", area="heap"}) 
    > ${saturation:value}
)

topk(${top}, 
    sum by (host_name) (jvm_memory_used{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", area="nonheap"})
    * 100 / 
    sum by (host_name) (jvm_memory_max{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", host_name=~"${host_name}", area="nonheap"}) 
    > ${saturation:value}
)
