targets.expr:
topk(${top}, 
    sum by(host_name, url_scheme, http_request_method, http_route, http_response_status_code, error_type) (rate(http_server_request_duration_count{service_name="${service_name}", service_version="${service_version}", deployment_environment="${deployment_environment}", http_response_status_code!~"[1-3].."}[1m]))
)

topk($top, 
    histogram_quantile(${percentile:value}, 
        sum(rate(http_server_request_duration_bucket{service_name="$service_name", deployment_environment="$deployment_environment"}[$__rate_interval])) by (le, http_request_method, url_scheme, host_name, http_route, http_response_status_code)
    )
)

topk($top, 
    histogram_quantile(${percentile:value}, 
        sum(rate(http_client_request_duration_bucket{service_name="$service_name", deployment_environment="$deployment_environment"}[$__rate_interval])) by (le, http_request_method, url_scheme, http_route, http_response_status_code,server_address,server_port,network_protocol_version)
    )
)
